        animalArrayList = ArrayList<Animals>()

        for (index in animalImages.indices){
            var animals = Animals(animalImages[index],animalNames[index],animalAbout[index])
            animalArrayList.add(animals)
        }
        myRecyclerView.layoutManager = LinearLayoutManager(this)
        var myAdapter = myAdapter(this,animalArrayList)
        myRecyclerView.adapter = myAdapter

        myAdapter.setItemClickListener(object :myAdapter.onItemClickListener{
            override fun onItemClick(position: Int) {
                Toast.makeText(this@MainActivity,"OnClickListener is Working",Toast.LENGTH_SHORT).show()

                val intent = Intent(this@MainActivity,Animal_Info::class.java)
                intent.putExtra("animalImage",animalArrayList[position].animalImg)
                intent.putExtra("animalNumber",animalArrayList[position].animalName)
                intent.putExtra("animalAbout",animalArrayList[position].animalAbout)

                startActivity(intent)

            }

        })

